INSERT INTO `stocklongdescriptiontranslations` VALUES ('VICSPON','de_DE.utf8','Ein klassischer Victoria-Sandwich-Kuchen. Der perfekte Party-Kuchen, ein Victoria-Schwamm, ist ein traditioneller Auflauf, den jeder lieben wird. ',1);
INSERT INTO `stocklongdescriptiontranslations` VALUES ('VICSPON','en_GB.utf8','A classic Victoria sandwich cake .The perfect party cake, a Victoria sponge is a traditional bake everyone will love. Makes an easy wedding cake too',0);
INSERT INTO `stocklongdescriptiontranslations` VALUES ('VICSPON','fr_FR.utf8','Un gâteau sandwich classique de Victoria. Le gâteau de fête parfait, une éponge Victoria est une cuisson traditionnelle que tout le monde adorera. Fait aussi un gâteau de mariage facile',0);
